const ratings = $input.first().json.ratings || $input.last().json.ratings;
const ratingsMap = Object.fromEntries(
  ratings.map(r => [r.anime_id, r.liked])
);

return $input.all()
  .filter(item => !item.json.ratings) // Only keep items that DON'T have ratings property
  .map(item => ({
    json: {
      ...item.json,
      liked: ratingsMap[item.json.id] || "False"
    }
  }));

const user_anime_ratings = {
  anime_ratings: $input.all().map(item => item.json)
}

return [
  {
    json: user_anime_ratings
  }
]

